DROP TABLE IF EXISTS `locales_gametips`;

CREATE TABLE `locales_gametips` (
  `entry` mediumint(8) NOT NULL,
  `tip_loc1` text,
  `tip_loc2` text,
  `tip_loc3` text,
  `tip_loc4` text,
  `tip_loc5` text,
  `tip_loc6` text,
  `tip_loc7` text,
  `tip_loc8` text,
  PRIMARY KEY (`entry`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

